import Button from "./components/UI/button";
import "./App.css";
import React, {useState} from "react";

function App() {

  const [isParagraphShown, setIsParagraphShown] = useState(false);

  const toogleParagraphHandler = () => {
    setIsParagraphShown(!isParagraphShown);
  }

  return (
    <div className="app">
      <h1>React под Капотом</h1>
      {isParagraphShown && <p>New Paragraph</p>}
      <Button onClick = {toogleParagraphHandler}>Показать Абзац</Button>
    </div>
  );
}

export default App;
